<script>
	$.urlParam = function (name)
	{
		var results = new RegExp('[\?&]' + name + '=([^&#]*)').exec(window.location.search);

		return (results !== null) ? results[1] || 0 : '';
	}

	$(function ()
	{
		$('#Account').val($.urlParam('account'));
		$('#PayAmount').val($.urlParam('amount'));
		$('#Mail').val($.urlParam('e-mail'));
	})
</script>
